import * as React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import useForm from '../hooks/form';
import useValidation from '../hooks/Validation';

const defaultTheme = createTheme();

const options = [
  { value: '', label: 'Are you attending with a guest?', disabled: true },
  { value: 'No', label: 'No' },
  { value: 'Yes', label: 'Yes' }
];

export default function EventRegistrationForm() {
  const initialValues = {
    name: '',
    email: '',
    age: '',
    attendingWithGuest: '',
    guestName: '',
  };

  const { values, handleChange, handleDropdownChange } = useForm(initialValues);
  const { errors, validate } = useValidation();
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    if (validate(values)) {
      navigate('/submitted', { state: { ...values } });
    }
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <Grid container component="main" sx={{ height: '100vh' }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: 'url(https://source.unsplash.com/random?wallpapers)',
            backgroundRepeat: 'no-repeat',
            backgroundColor: (t) =>
              t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Event Registration
            </Typography>
            <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="name"
                label="Name"
                name="name"
                autoComplete="name"
                value={values.name}
                onChange={handleChange}
                autoFocus
              />
              {errors.name && <Typography color="error">{errors.name}</Typography>}
              
              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                value={values.email}
                onChange={handleChange}
              />
              {errors.email && <Typography color="error">{errors.email}</Typography>}
              
              <TextField
                margin="normal"
                required
                fullWidth
                name="age"
                label="Age"
                type="number"
                id="age"
                value={values.age}
                onChange={handleChange}
              />
              {errors.age && <Typography color="error">{errors.age}</Typography>}

              <Dropdown
                options={options}
                onChange={(option) => handleDropdownChange('attendingWithGuest', option.value)}
                value={values.attendingWithGuest ? options.find(opt => opt.value === values.attendingWithGuest) : options[0]}
                placeholder="Are you attending with a guest?"
              />
              {errors.attendingWithGuest && (
                <Typography color="error">{errors.attendingWithGuest}</Typography>
              )}

              {values.attendingWithGuest === 'Yes' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="guestName"
                  label="Guest Name"
                  id="guestName"
                  value={values.guestName}
                  onChange={handleChange}
                />
              )}
              {values.attendingWithGuest === 'Yes' && errors.guestName && (
                <Typography color="error">{errors.guestName}</Typography>
              )}

              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
              >
                Register
              </Button>
              <Grid container>
                <Grid item xs>
                  <Link href="#" variant="body2">
                    Forgot password?
                  </Link>
                </Grid>
                <Grid item>
                  <Link href="#" variant="body2">
                    {"Don't have an account? Sign Up"}
                  </Link>
                </Grid>
              </Grid>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
}
