import * as React from 'react';
import {useNavigate} from 'react-router-dom';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import useForm from '../hooks/useForm'
import useValidation from '../hooks/Validation';

const defaultTheme = createTheme();

export default function EventRegistrationForm() {
  const initialValues = {
    name: '',
    email: '',
    age: '',
    attendingWithGuest: '',
    guestName: '',
  };

  const { values, handleChange } = useForm(initialValues);
  const { errors, validate } = useValidation();
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    if (validate(values)) {
      navigate('/submitted', { state: { ...values } });
    }
  };

  return (

    <ThemeProvider theme={defaultTheme}>
      <div class="absolute inset-0 -z-10 h-full w-full items-center px-5 py-24 [background:radial-gradient(125%_125%_at_50%_10%,#000_40%,#63e_100%)]"></div>
      <Grid container component="main" sx={{ height: '100vh' }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: 'url(https://source.unsplash.com/random?wallpapers)',
            backgroundRepeat: 'no-repeat',
            backgroundColor: (t) =>
              t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              backgroundColor: (t) =>
                t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
              boxShadow: defaultTheme.shadows[6],
              padding: defaultTheme.spacing(4),
              position: 'relative', // Ensure absolute positioning works correctly
           // Ensure the radial gradient doesn't overflow
              backgroundImage: 'radial-gradient(125% 125% at 50% 10%, #000 40%, #63e 100%)',
              height: '100%', // Ensure the form side takes full height
              width: '100%', // Ensure the form side takes full width
              top: 0,
              left: 0,
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Event Registration
            </Typography>
            <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="name"
                label="Name"
                name="name"
                autoComplete="name"
                value={values.name}
                onChange={handleChange}
                autoFocus
              />
              {errors.name && <Typography color="error">{errors.name}</Typography>}
              
              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                value={values.email}
                onChange={handleChange}
              />
              {errors.email && <Typography color="error">{errors.email}</Typography>}
              
              <TextField
                margin="normal"
                required
                fullWidth
                name="age"
                label="Age"
                type="number"
                id="age"
                value={values.age}
                onChange={handleChange}
              />
              {errors.age && <Typography color="error">{errors.age}</Typography>}

              <FormControl fullWidth margin="normal" required>
                <InputLabel id="attendingWithGuest-label">Are you attending with a guest?</InputLabel>
                <Select
                  labelId="attendingWithGuest-label"
                  id="attendingWithGuest"
                  name="attendingWithGuest"
                  value={values.attendingWithGuest}
                  onChange={handleChange}
                  label="Are you attending with a guest?"
                >
                  <MenuItem value="Yes">Yes</MenuItem>
                  <MenuItem value="No">No</MenuItem>
                </Select>
              </FormControl>
              {errors.attendingWithGuest && (
                <Typography color="error">{errors.attendingWithGuest}</Typography>
              )}

              {values.attendingWithGuest === 'Yes' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="guestName"
                  label="Guest Name"
                  id="guestName"
                  value={values.guestName}
                  onChange={handleChange}
                />
              )}
              {values.attendingWithGuest === 'Yes' && errors.guestName && (
                <Typography color="error">{errors.guestName}</Typography>
              )}

              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
              >
                Register
              </Button>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
}
