import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Copyright from './Copyright';
import useForm from '../hooks/useForm';
import useValidation from '../hooks/useValidation';

const defaultTheme = createTheme();

function Form() {
    const initialValues = {
        name: '',
        email: '',
        age: '',
        attendingWithGuest: false,
        guestName: '',
      };
    
      const { values, handleChange } = useForm(initialValues);
      const { errors, validate } = useValidation();
    
      const handleSubmit = (event) => {
        event.preventDefault();
        if (validate(values)) {
          alert(JSON.stringify(values, null, 2));
        }
      };
    
      return (
        <ThemeProvider theme={defaultTheme}>
          <Grid container component="main" sx={{ height: '100vh' }}>
            <CssBaseline />
            <Grid
              item
              xs={false}
              sm={4}
              md={7}
              sx={{
                backgroundImage: 'url(https://source.unsplash.com/random?wallpapers)',
                backgroundRepeat: 'no-repeat',
                backgroundColor: (t) =>
                  t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
                backgroundSize: 'cover',
                backgroundPosition: 'center',
              }}
            />
            <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
              <Box
                sx={{
                  my: 8,
                  mx: 4,
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                }}
              >
                <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
                  <LockOutlinedIcon />
                </Avatar>
                <Typography component="h1" variant="h5">
                  Event Registration
                </Typography>
                <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
                  <TextField
                    margin="normal"
                    required
                    fullWidth
                    id="name"
                    label="Name"
                    name="name"
                    autoComplete="name"
                    value={values.name}
                    onChange={handleChange}
                    autoFocus
                  />
                  {errors.name && <Typography color="error">{errors.name}</Typography>}
                  
                  <TextField
                    margin="normal"
                    required
                    fullWidth
                    id="email"
                    label="Email Address"
                    name="email"
                    autoComplete="email"
                    value={values.email}
                    onChange={handleChange}
                  />
                  {errors.email && <Typography color="error">{errors.email}</Typography>}
                  
                  <TextField
                    margin="normal"
                    required
                    fullWidth
                    name="age"
                    label="Age"
                    type="number"
                    id="age"
                    value={values.age}
                    onChange={handleChange}
                  />
                  {errors.age && <Typography color="error">{errors.age}</Typography>}
    
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={values.attendingWithGuest}
                        onChange={handleChange}
                        name="attendingWithGuest"
                        color="primary"
                      />
                    }
                    label="Are you attending with a guest?"
                  />
    
                  {values.attendingWithGuest && (
                    <TextField
                      margin="normal"
                      required
                      fullWidth
                      name="guestName"
                      label="Guest Name"
                      id="guestName"
                      value={values.guestName}
                      onChange={handleChange}
                    />
                  )}
                  {values.attendingWithGuest && errors.guestName && (
                    <Typography color="error">{errors.guestName}</Typography>
                  )}
    
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    sx={{ mt: 3, mb: 2 }}
                  >
                    Register
                  </Button>
                  <Grid container>
                    <Grid item xs>
                      <Link href="#" variant="body2">
                        Forgot password?
                      </Link>
                    </Grid>
                    <Grid item>
                      <Link href="#" variant="body2">
                        {"Don't have an account? Sign Up"}
                      </Link>
                    </Grid>
                  </Grid>
                  <Copyright sx={{ mt: 5 }} />
                </Box>
              </Box>
            </Grid>
          </Grid>
        </ThemeProvider>
      );
    }


export default Form
