import React from 'react';
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import TextField from '@mui/material/TextField';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import PersonIcon from '@mui/icons-material/Person';
import EmailIcon from '@mui/icons-material/Email';
import EventIcon from '@mui/icons-material/Event';
import GroupIcon from '@mui/icons-material/Group';
import useForm from '../hooks/'

const EventRegistrationForm = () => {
  const initialValues = {
    name: '',
    email: '',
    age: '',
    attendingWithGuest: '',
    guestName: '',
  };

  const { values, handleChange } = useForm(initialValues);
  const { errors, validate } = useValidation();
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    if (validate(values)) {
      navigate('/submitted', { state: { ...values } });
    }
  };

  const handleRegisterClick = () => {
    if (validate(values)) {
      navigate('/submitted', { state: { ...values } });
    }
  };

  return (
    <Paper elevation={3} style={{ padding: 16, margin: 'auto', maxWidth: 600 }}>
      <Box
        sx={{
          my: 8,
          mx: 4,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <LockOutlinedIcon sx={{ fontSize: 48, mb: 2 }} />
        <Typography component="h1" variant="h5">
          Event Registration
        </Typography>
        <form onSubmit={handleSubmit} style={{ width: '100%', marginTop: 32 }}>
          <Box sx={{ mb: 2 }}>
            <TextField
              id="name"
              name="name"
              label="Name"
              variant="outlined"
              fullWidth
              value={values.name}
              onChange={handleChange}
              InputProps={{
                startAdornment: (
                  <PersonIcon sx={{ color: 'action.active', mr: 1, my: 0.5 }} />
                ),
              }}
              error={!!errors.name}
              helperText={errors.name}
              required
            />
          </Box>

          <Box sx={{ mb: 2 }}>
            <TextField
              id="email"
              name="email"
              label="Email Address"
              variant="outlined"
              fullWidth
              value={values.email}
              onChange={handleChange}
              InputProps={{
                startAdornment: (
                  <EmailIcon sx={{ color: 'action.active', mr: 1, my: 0.5 }} />
                ),
              }}
              error={!!errors.email}
              helperText={errors.email}
              required
            />
          </Box>

          <Box sx={{ mb: 2 }}>
            <TextField
              id="age"
              name="age"
              label="Age"
              type="number"
              variant="outlined"
              fullWidth
              value={values.age}
              onChange={handleChange}
              InputProps={{
                startAdornment: (
                  <EventIcon sx={{ color: 'action.active', mr: 1, my: 0.5 }} />
                ),
              }}
              error={!!errors.age}
              helperText={errors.age}
              required
            />
          </Box>

          <FormControl fullWidth variant="outlined" sx={{ mb: 2 }} required>
            <InputLabel id="attendingWithGuest-label">Are you attending with a guest?</InputLabel>
            <Select
              labelId="attendingWithGuest-label"
              id="attendingWithGuest"
              name="attendingWithGuest"
              value={values.attendingWithGuest}
              onChange={handleChange}
              label="Are you attending with a guest?"
              startAdornment={<GroupIcon sx={{ color: 'action.active', mr: 1, my: 0.5 }} />}
            >
              <MenuItem value="Yes">Yes</MenuItem>
              <MenuItem value="No">No</MenuItem>
            </Select>
          </FormControl>

          {values.attendingWithGuest === 'Yes' && (
            <Box sx={{ mb: 2 }}>
              <TextField
                id="guestName"
                name="guestName"
                label="Guest Name"
                variant="outlined"
                fullWidth
                value={values.guestName}
                onChange={handleChange}
                InputProps={{
                  startAdornment: (
                    <PersonIcon sx={{ color: 'action.active', mr: 1, my: 0.5 }} />
                  ),
                }}
                error={!!errors.guestName}
                helperText={errors.guestName}
                required
              />
            </Box>
          )}

          <div className="relative px-5 py-3 overflow-hidden font-medium text-gray-600 bg-gray-100 border border-gray-100 rounded-lg shadow-inner group"
            style={{
              marginTop: '20px', // Adjust the margin as needed
              textAlign: 'center',
              position: 'relative',
              zIndex: 1,
            }}
            onClick={handleRegisterClick}
          >
            <span className="absolute top-0 left-0 w-0 h-0 transition-all duration-200 border-t-2 border-gray-600 group-hover:w-full ease"></span>
            <span className="absolute bottom-0 right-0 w-0 h-0 transition-all duration-200 border-b-2 border-gray-600 group-hover:w-full ease"></span>
            <span className="absolute top-0 left-0 w-full h-0 transition-all duration-300 delay-200 bg-gray-600 group-hover:h-full ease"></span>
            <span className="absolute bottom-0 left-0 w-full h-0 transition-all duration-300 delay-200 bg-gray-600 group-hover:h-full ease"></span>
            <span className="absolute inset-0 w-full h-full duration-300 delay-300 bg-gray-900 opacity-0 group-hover:opacity-100"></span>
            <span className="relative transition-colors duration-300 delay-200 group-hover:text-white ease" style={{ zIndex: 2 }}>Register</span>
          </div>

        </form>
      </Box>
    </Paper>
  );
};

export default EventRegistrationForm;
